mapper.select:
SELECT room_agora_token
FROM room
WHERE room_name=#{roomName}

mapper.update:
UPDATE room
SET room_agora_token=#{roomAgoraToken}
WHERE room_name=#{roomName}

mapper.select:
SELECT room_agora_uid
FROM room
WHERE room_name=#{roomName}

mapper.update:
UPDATE room
SET room_agora_uid=#{roomAgoraUid}
WHERE room_name=#{roomName}
