mapper.select:
SELECT user_name, user_skin_color, user_skin_role
FROM user
WHERE user_id=#{userId};

SELECT user_id, user_skin_color, user_skin_role
FROM user
WHERE user_name=#{userName};

SELECT room_name 
FROM room 
WHERE room_code = #{roomCode}

SELECT room_code 
FROM room 
WHERE room_name = #{roomName}

SELECT room_name
FROM room
WHERE room_name = #{roomName}

mapper.update:
UPDATE user
SET user_auth_token=#{authToken}
WHERE user_id=#{userId}

UPDATE room
SET room_code=#{roomCode}
WHERE global_room_no=#{roomNo}
  AND room_name=#{roomName}

UPDATE room
SET room_present = room_present+1
WHERE room_name=#{roomName}

UPDATE room
SET room_present = room_present-1
WHERE room_name=#{roomName}
