mapper.select:
SELECT room_name 
FROM room 
WHERE room_code = #{roomCode}

SELECT room_code 
FROM room 
WHERE room_name = #{roomName}

SELECT room_name
FROM room
WHERE room_name = #{roomName}

mapper.update:
UPDATE room
SET room_code=#{roomCode}
WHERE global_room_no=#{roomNo}
  AND room_name=#{roomName}

UPDATE room, joining
SET room.room_present = room.room_present+1,
joining.joining_recent_dt = NOW()
WHERE room.room_name=#{roomName}
AND joining.joining_user_no=(
  SELECT global_user_no 
  FROM user 
  WHERE user_id = #{userId})
AND joining.joining_room_no=(
  SELECT global_room_no
  FROM room 
  WHERE room_name = #{roomName}
);

mapper.select:
SELECT room_present
FROM room
WHERE room_name=#{roomName}
